"use strict";

var router = require('express').Router();
var appCtrl = require('../controllers/AppController');
var userCtrl = require('../controllers/UsersController');
var authCtrl = require('../controllers/AuthController');
var fileCtrl = require('../controllers/FilesController');
var urlDecoder = require('qs');
router.get('/status', function (req, res) {
  appCtrl.getStatus(res);
});
router.get('/stats', function (req, res) {
  appCtrl.getStats(res);
});
router.post('/users', function (req, res) {
  req.on('data', function (i) {
    var data = JSON.parse(String(i));
    if (!data.email) {
      res.status(400).send({
        "error": "Missing email"
      });
    }
    if (!data.password) {
      res.status(400).send({
        "error": "Missing password"
      });
    }
    if (data.email && data.password) {
      userCtrl.postNew(req, res, data.email, data.password);
    }
  });
});
router.get('/connect', function (req, res) {
  authCtrl.getConnect(req, res);
});
router.get('/disconnect', function (req, res) {
  authCtrl.getDisconnect(req, res);
});
router.get('/users/me', function (req, res) {
  userCtrl.getMe(req, res);
});
router.post('/files', function (req, res) {
  fileCtrl.postUpload(req, res);
});
module.exports = router;
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
const router = require('express').Router();
const appCtrl = require('../controllers/AppController');
const userCtrl = require('../controllers/UsersController');
const authCtrl = require('../controllers/AuthController');
const fileCtrl = require('../controllers/FilesController');
const urlDecoder = require('qs');
router.get('/status', (req, res)=>{
  appCtrl.getStatus(res);
})
router.get('/stats', (req, res)=>{
  appCtrl.getStats(res);
})
router.post('/users', (req, res)=>{
req.on('data', (i)=>{
  let data = JSON.parse(String(i));
  if (!data.email){
    res.status(400).send({"error":"Missing email"});
  }
  if (!data.password){
    res.status(400).send({"error":"Missing password"});
  }
  if (data.email && data.password){
    userCtrl.postNew(req, res, data.email, data.password);
  }
})
})
router.get('/connect', (req, res)=>{
  authCtrl.getConnect(req, res);
})
router.get('/disconnect', (req, res)=>{
  authCtrl.getDisconnect(req, res);
})
router.get('/users/me', (req, res)=>{
  userCtrl.getMe(req, res);
})
router.post('/files', (req, res)=>{
  fileCtrl.postUpload(req, res);
})
module.exports = router;

